mapper.select:
select count(1) from opinion where 1=1
AND INTRO like '%${intro}%'
AND STATUS = #{status}

select opinion.id, intro, details,name, creator_id as creatorId, create_time as createTime, status from opinion,user where 1=1 and user.id = opinion.CREATOR_ID
AND INTRO like '%${intro}%'
AND STATUS = #{status}
order by create_time desc limit ${start},${limit}

mapper.delete:
delete from opinion
WHERE ID in

    #{id}

mapper.update:
update opinion
set INTRO   = #{intro},
    DETAILS = #{details}
where ID = #{id}
