mapper.select:
select count(1) from opinion a,user b
where a.CREATOR_ID = b.id

    AND b.ID =#{userId}


    AND a.INTRO like '%${intro}%'


    AND a.STATUS = #{status}

select a.id as id, a.intro as intro, a.details as details, a.creator_id as creatorId, a.create_time as createTime, a.status as status, b.name as name, b.RNAME as rname from opinion a,user b where a.CREATOR_ID = b.id
AND a.ID =#{id}
AND b.ID =#{userId}
AND a.INTRO like '%${intro}%'
AND a.STATUS = #{status}
order by a.create_time desc
limit ${start},${limit}

mapper.delete:
delete from opinion
WHERE ID in

    #{id}

mapper.update:
update opinion
set INTRO   = #{intro},
    DETAILS = #{details}
where ID = #{id}
