mapper.select:
select count(1) from outin

    AND start_time = #{startTime}


    AND end_time = #{endTime}


    AND type = #{type}

select id as id,
name as name,
PHONE as phone,
LICENSE as license,
START_TIME as startTime,
END_TIME as endTime,
TYPE as type
from outin
where 1=1

    AND ID =#{id}


    AND start_time = #{startTime}


    AND end_time = #{endTime}


    AND type = #{type}

order by START_TIME desc

    limit ${start},${limit}

mapper.update:
update outin
set NAME=#{name},PHONE=#{phone},LICENSE=#{license}, START_TIME = #{startTime},END_TIME=#{endTime},TYPE=#{type}
where ID = #{id}

mapper.delete:
delete from outin
WHERE ID in

    #{id}
