mapper.select:
select count(1) from pay a,user b,user c where a.PAYER_ID = b.ID and a.PAYEE_ID = c.ID
AND b.RNAME like '%${payerRname}%'
AND c.RNAME = #{payeeRname}

mapper.delete:
delete from pay
WHERE ID in

    #{id}

mapper.update:
update pay
set TEXT_MONEY  = #{textMoney},
    VALUE_MONEY = #{valueMoney},
    TAX=#{tax},
    COMMENT=#{comment}
where ID = #{id}
