mapper.select:
select id, name, pwd, phone, address, rname, CREATE_TIME as createTime
from user
where NAME = #{name}

select a.ID as id, a.name as name, a.PWD as pwd, a.phone as phone, a.ADDRESS as address, a.RNAME as rname, a.CREATE_TIME as createTime, c.ID as roleId, c.NAME as roleName from user a,user_role b,role c where a.id = b.USER_ID and c.ID = b.ROLE_ID
AND a.name like '%${name}%'
AND a.rname like '%${rname}%'
AND c.ID = #{roleId}
order by a.ID
limit ${start},${limit}

mapper.update:
update user
set pwd     = #{pwd},
    PHONE   = #{phone},
    ADDRESS = #{address},
    RNAME   = #{rname}
where ID = #{id}

update user_role
set ROLE_ID = #{roleId}
where USER_ID = #{userId}

mapper.delete:
delete from user
WHERE ID in

    #{id}
